mapper.select:
SELECT *, (SELECT GROUP_CONCAT(image_list SEPARATOR ', ') FROM post_image_list WHERE post_id = p.id GROUP BY post_id) AS image_list, (SELECT COUNT(pl.user_id) FROM db02.post_like pl WHERE pl.post_id = p.id AND pl.status = 1 GROUP BY post_id) AS like_count, (SELECT COUNT(pl.user_id) FROM db02.post_like pl WHERE pl.post_id = p.id AND pl.status = 2 GROUP BY post_id) AS dislike_count, (SELECT COUNT(id) FROM db02.comment cm WHERE cm.post_id = p.id AND cm.del_flg = 0 GROUP BY post_id) AS comment_count, (SELECT COUNT(pl.user_id) FROM db02.post_like pl WHERE pl.post_id = p.id AND pl.status = 1 AND pl.user_id = #{userId} GROUP BY post_id) AS is_like, (SELECT COUNT(pl.user_id) FROM db02.post_like pl WHERE pl.post_id = p.id AND pl.status = 2 AND pl.user_id = #{userId} GROUP BY post_id) AS is_dislike, (SELECT COUNT(pl.user_id) FROM db02.post_view pl WHERE pl.post_id = p.id) AS view_count FROM post p WHERE
p.`${key}` like '%${value}%'
AND p.delflg = 0 AND p.status = 1 ORDER BY p.create_at DESC LIMIT 10 OFFSET #{offset}
